CREATE TABLE mdgalledy_album (id BIGINT AUTO_INCREMENT, author_id INT, title VARCHAR(100) NOT NULL, description LONGTEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX author_id_idx (author_id), PRIMARY KEY(id)) ENGINE = INNODB;
